import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { Link } from "react-router-dom";
import styled from 'styled-components';
import AuthContext from "./AuthContext";
import tt from './exp.png';

export default function Login(props) {
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const history = useNavigate();
  const { login } = useContext(AuthContext);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:5000/api/auth/login", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email: credentials.email, password: credentials.password })
    });
    const json = await response.json();
    if (json.success) {
      localStorage.setItem('token', json.authtoken);
      alert(`${credentials.email} has been logged in`);
      login(credentials);
      history('/dashboard');
    } else {
      alert("Invalid credentials");
    }
  };

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  return (
    <LoginContainer>
      <LeftSection>
        <h2>Welcome Back!</h2>
        <p>Login to access your account and manage your finances.</p>
      </LeftSection>
      <RightSection>
        <h1><img src={tt} alt="Loading" width={`125`} height={`125`} /></h1>
        <h1 className={`text-${props.mode === "light" ? "rgb(43, 45, 106)" : "white"}`}>Login</h1>
        <div className="form-container">
          <form action="POST" className="my-3" onSubmit={handleSubmit}>
            <div className={`row mb-3 text-${props.mode === 'light' ? 'dark' : 'light'}`}>
              <div className="col-sm-12">
                <input type="email" id='email' value={credentials.email} name='email' onChange={onChange} className="form-control" placeholder="Email" />
              </div>
            </div>

            <div className="row mb-3">
              <div className="col-sm-12">
                <input type="password" id='password' name='password' value={credentials.password} className="form-control" onChange={onChange} placeholder="Password" />
              </div>
            </div>
            <div>
              <input type="submit" className="btn btn-primary mx-2 my-2" />
              <Link to="/signup" className="btn btn-primary bg-chocolate">Click here to SignUp</Link>
            </div>
          </form>
        </div>
      </RightSection>
    </LoginContainer>
  );
}

const LoginContainer = styled.div`
  display: flex;
  height: 100vh;
  justify-content: center;
  align-items: center;
  background-color: rgb(255, 255, 255);
`;

const LeftSection = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 20px;
  background-color: #f5f5f5;
  box-shadow: inset 0px 0px 10px rgba(0, 0, 0, 0.1);

  h2 {
    font-size: 2rem;
    color: rgb(43, 45, 106);
  }

  p {
    font-size: 1.2rem;
    color: #555;
    text-align: center;
  }
`;

const RightSection = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 20px;
  background-color: #ffffff;
  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.1);
  border-radius: 10px;

  .form-container {
    width: 100%;
  }

  .form-control {
    width: 100%;
    padding: 10px;
    margin: 5px 0;
  }

  .btn-primary {
    background-color: rgb(16, 124, 227);
  }

  h1 {
    margin-bottom: 20px;
    color: rgb(43, 45, 106);
  }
`;
