import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { Link } from "react-router-dom";
import './Login.css';  // Reusing the same CSS for consistent styling
import AuthContext from "./AuthContext";
import tt from './exp.png';

export default function Signup(props) {
  const [credentials, setCredentials] = useState({ email: "", password: "", confirmPassword: "" });
  const history = useNavigate();
  const { register } = useContext(AuthContext);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (credentials.password !== credentials.confirmPassword) {
      alert("Passwords do not match");
      return;
    }
    const response = await fetch("http://localhost:5000/api/auth/signup", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email: credentials.email, password: credentials.password })
    });
    const json = await response.json();
    if (json.success) {
      localStorage.setItem('token', json.authtoken);
      alert(`${credentials.email} has been successfully registered`);
      register(credentials);
      history('/dashboard');
    } else {
      alert("Registration failed");
    }
  };

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  return (
    <div className="login-container">
      <div className="left-section">
        <h2>Join Us!</h2>
        <p>Create an account to manage your finances efficiently.</p>
      </div>
      <div className="right-section">
        <h1><img src={tt} alt="Loading" width={`125`} height={`125`} /></h1>
        <h1 className={`text-${props.mode === "light" ? "rgb(43, 45, 106)" : "white"}`}>Signup</h1>
        <div className="form-container">
          <form action="POST" className="my-3" onSubmit={handleSubmit}>
            <div className={`row mb-3 text-${props.mode === 'light' ? 'dark' : 'light'}`}>
              <div className="col-sm-12">
                <input type="email" id='email' value={credentials.email} name='email' onChange={onChange} className="form-control" placeholder="Email" />
              </div>
            </div>

            <div className="row mb-3">
              <div className="col-sm-12">
                <input type="password" id='password' name='password' value={credentials.password} className="form-control" onChange={onChange} placeholder="Password" />
              </div>
            </div>

            <div className="row mb-3">
              <div className="col-sm-12">
                <input type="password" id='confirmPassword' name='confirmPassword' value={credentials.confirmPassword} className="form-control" onChange={onChange} placeholder="Confirm Password" />
              </div>
            </div>

            <div>
              <input type="submit" className="btn btn-primary mx-2 my-2" />
              <Link to="/" className="btn btn-primary bg-chocolate">Already have an account? Login</Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
