import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import tt from './exp.png';

// Inline styles
const containerStyle = {
  display: 'flex',
  height: '100vh',
  justifyContent: 'center',
  alignItems: 'center',
  backgroundColor: 'rgb(255, 255, 255)',
  paddingRight: '50px',
};

const leftSectionStyle = {
  flex: 1,
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  alignItems: 'center',
  padding: '20px',
  backgroundColor: '#ffffff',
};

const leftSectionHeadingStyle = {
  fontSize: '2rem',
  color: 'rgb(43, 45, 106)',
};

const leftSectionParaStyle = {
  fontSize: '1.2rem',
  color: '#555',
  textAlign: 'center',
};

const rightSectionStyle = {
  flex: 1,
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  alignItems: 'center',
  padding: '20px',
  backgroundColor: '#ffffff',
  boxShadow: '0px 0px 20px rgba(0, 0, 0, 0.1)',
  borderRadius: '10px',
};

const formContainerStyle = {
  width: '50%',
};

const formControlStyle = {
  width: '100%',
  padding: '100px',
  margin: '5px 0',
};

const btnPrimaryStyle = {
  backgroundColor: 'rgb(16, 124, 227)',
};

const headingStyle = {
  marginBottom: '20px',
  color: 'rgb(43, 45, 106)',
};

export default function Signup(props) {
  const [credentials, setCredentials] = useState({ name: "", email: "", password: "" });
  const history = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { name, email, password } = credentials;
    const response = await fetch("http://localhost:5000/api/auth/createuser", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ name, email, password })
    });
    const json = await response.json();
    if (json.success) {
      localStorage.setItem('token', json.authtoken);
      history("/");
      alert("Registration Successful!!");
    } else {
      alert("Invalid credentials");
    }
  }

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  }

  return (
    <div style={containerStyle}>
      <div style={leftSectionStyle} className="left-section">
        <h2 style={leftSectionHeadingStyle}>Join Us Today!</h2>
        <p style={leftSectionParaStyle}>Create an account to start managing your finances.</p>
      </div>
      <div style={rightSectionStyle} className="right-section">
        <h1 style={headingStyle}><img src={tt} alt="Loading" width={125} height={125} /></h1>
        <h1 className={`text-${props.mode === "light" ? "rgb(43, 45, 106)" : "white"}`}>SignUp</h1>
        <div style={formContainerStyle} className="form-container">
          <form action="POST" className="my-3" onSubmit={handleSubmit}>
            <div className="row mb-3">
              <div className="col-sm-5">
                <input type="text" id="name" name='name' onChange={onChange} style={formControlStyle} placeholder="Username" />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-sm-5">
                <input type="email" id="email" name='email' onChange={onChange} style={formControlStyle} placeholder="Email" />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-sm-5">
                <input type="password" id="password" name='password' onChange={onChange} style={formControlStyle} placeholder="Password" />
              </div>
            </div>
            <div>
              <input type="submit" style={btnPrimaryStyle} className="btn btn-primary mx-2 my-2" />
              <Link to="/" className="btn btn-primary">Click here to Login</Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
